package Xue;

public class Xarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] a=new int[]{1,2,3,4,5,6,7,8,9,0};
		int phone = a[0];
		System.out.println(phone);
		int num = 0;
		int i=1;
		for(i=1;i<=999;i++){
			num += i;
			
		}
		System.out.println(num);
	}

}
